import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import * as XLSX from 'xlsx';

const RegisterPage = () => {
  const [formData, setFormData] = useState({
    rollNumber: '',
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
  });

  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Load name from marks.xlsx based on roll number
  const handleRollSearch = async () => {
    setFormData({ ...formData, name: '' });
    try {
      const response = await fetch('/marks.xlsx');
      const data = await response.arrayBuffer();
      const workbook = XLSX.read(data, { type: 'array' });
      const sheet = workbook.Sheets[workbook.SheetNames[0]];
      const rows = XLSX.utils.sheet_to_json(sheet);
      const student = rows.find(row => String(row.RollNumber).trim() === formData.rollNumber.trim());

      if (student) {
        setFormData(prev => ({ ...prev, name: student.StudentName }));
        setError(null);
      } else {
        setError('Roll number not found in marks.xlsx');
      }
    } catch (err) {
      console.error(err);
      setError('Error reading marks.xlsx');
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleRegister = async (e) => {
    e.preventDefault();

    if (formData.password !== formData.confirmPassword) {
      setError("Passwords do not match");
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/auth/register', {
        name: formData.name,
        rollNumber: formData.rollNumber,
        email: formData.email,
        password: formData.password,
      });

      localStorage.setItem('token', response.data.token);
      navigate('/login');
    } catch (err) {
      setError(err.response?.data?.message || 'Registration failed');
      console.error('Registration error:', err);
    }
  };

  // Styling based on the referral page colors (soft blues, whites, subtle grays)
  const containerStyle = {
    height: '100vh',
    background: 'linear-gradient(135deg, #e0f2fe, #bae6fd)', // soft blue gradient
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    color: '#1e3a8a', // dark blue text
  };

  const formStyle = {
    backgroundColor: '#ffffff',
    padding: '40px',
    borderRadius: '16px',
    boxShadow: '0 4px 20px rgba(59, 130, 246, 0.3)', // subtle blue shadow
    width: '100%',
    maxWidth: '440px',
  };

  const inputStyle = {
    width: '100%',
    padding: '12px',
    margin: '12px 0',
    borderRadius: '8px',
    border: '1.5px solid #93c5fd', // light blue border
    fontSize: '1rem',
    color: '#1e3a8a',
  };

  const buttonStyle = {
    width: '100%',
    padding: '14px',
    marginTop: '20px',
    borderRadius: '10px',
    border: 'none',
    backgroundColor: '#3b82f6', // primary blue
    color: '#fff',
    fontSize: '1.1rem',
    fontWeight: '600',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  };

  const autofillButtonStyle = {
    ...buttonStyle,
    backgroundColor: '#60a5fa', // lighter blue for autofill
    marginTop: '8px',
  };

  const linkStyle = {
    display: 'block',
    textAlign: 'center',
    marginTop: '18px',
    color: '#2563eb',
    textDecoration: 'underline',
    cursor: 'pointer',
  };

  const headingStyle = {
    textAlign: 'center',
    marginBottom: '24px',
    fontWeight: '700',
    fontSize: '2rem',
  };

  const collegeNameStyle = {
    textAlign: 'center',
    fontSize: '0.9rem',
    color: '#2563eb',
    marginBottom: '30px',
    fontWeight: '600',
  };

  return (
    <div style={containerStyle}>
      <form onSubmit={handleRegister} style={formStyle}>

        <div style={collegeNameStyle}>Vignan's Institute of Technology & Science (VGNT)</div>

        <h2 style={headingStyle}>Register</h2>

        {error && <p style={{ color: '#dc2626', textAlign: 'center' }}>{error}</p>}

        <input
          type="text"
          name="rollNumber"
          placeholder="Roll Number"
          value={formData.rollNumber}
          onChange={handleChange}
          style={inputStyle}
          required
        />
        <button type="button" onClick={handleRollSearch} style={autofillButtonStyle}>
          Autofill Name
        </button>

        <input
          type="text"
          name="name"
          placeholder="Full Name"
          value={formData.name}
          onChange={handleChange}
          style={inputStyle}
          readOnly
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          style={inputStyle}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          style={inputStyle}
          required
        />
        <input
          type="password"
          name="confirmPassword"
          placeholder="Confirm Password"
          value={formData.confirmPassword}
          onChange={handleChange}
          style={inputStyle}
          required
        />

        <button type="submit" style={buttonStyle}>Register</button>

        <Link to="/login" style={linkStyle}>Already have an account? Login</Link>
      </form>
    </div>
  );
};

export default RegisterPage;
