import React from 'react';

function MissingPersonPoster() {
  return (
    <div style={{
      width: '400px',
      margin: '40px auto',
      border: '5px solid red',
      fontFamily: 'Arial, sans-serif',
      textAlign: 'center',
      backgroundColor: '#fff'
    }}>
      <div style={{
        backgroundColor: 'red',
        color: 'white',
        padding: '20px 10px',
        fontSize: '36px',
        fontWeight: 'bold',
        letterSpacing: '3px'
      }}>
        MISSING
      </div>

      <img
        src="/Passport_Size_Photo.jpg" // Your image should be in public/ folder as missing-person.png
        alt="Missing Person"
        style={{
          width: '100%',
          height: 'auto'
        }}
      />

      <div style={{
        fontWeight: 'bold',
        fontSize: '22px',
        padding: '10px 0',
        borderBottom: '2px solid black'
      }}>
        Ravula Abhinav Reddy
      </div>

      <div style={{
        backgroundColor: 'red',
        color: 'white',
        padding: '20px',
        fontSize: '16px',
        lineHeight: '1.6'
      }}>
        HAVE YOU SEEN THIS PERSON?<br />
        Please contact Mr.Rajesh Reddy<br />
        <br />
        <strong>Mobile no:</strong><br />
        9959965366
      </div>
    </div>
  );
}

export default MissingPersonPoster;
