import React from 'react';
import { useNavigate } from 'react-router-dom';

const DepartmentPage = () => {
  const navigate = useNavigate();

  const handleStudentClick = () => {
    navigate('/login'); // or navigate to student portal
  };

  // Styles
  const containerStyle = {
    minHeight: '100vh',
    backgroundColor: '#0f172a', // Tailwind slate-900
    color: '#f1f5f9', // Tailwind slate-100
    fontFamily: 'Segoe UI, Tahoma, Geneva, Verdana, sans-serif',
    display: 'flex',
    flexDirection: 'column',
  };

  const headerStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '1rem 1.5rem',
    borderBottom: '1px solid #334155', // Tailwind slate-700
  };

  const headerTitleStyle = {
    fontSize: '1.875rem', // 3xl
    fontWeight: '700',
    color: '#2563eb', // Tailwind blue-600
  };

  const buttonStyle = {
    backgroundColor: '#2563eb', // Tailwind blue-600
    color: 'white',
    padding: '0.5rem 1rem',
    borderRadius: '0.375rem', // rounded-md
    fontWeight: '600',
    border: 'none',
    cursor: 'pointer',
    transition: 'background-color 0.2s',
  };

  const buttonHoverStyle = {
    backgroundColor: '#1d4ed8', // Tailwind blue-700
  };

  const mainStyle = {
    padding: '3rem 2rem',
    flexGrow: 1,
  };

  const sectionCenterStyle = {
    textAlign: 'center',
    marginBottom: '3.5rem',
    maxWidth: '48rem',
    marginLeft: 'auto',
    marginRight: 'auto',
  };

  const heading1Style = {
    fontSize: '2.25rem', // 4xl
    fontWeight: '700',
    marginBottom: '1rem',
    color: '#f1f5f9', // slate-100
  };

  const paragraphStyle = {
    color: '#cbd5e1', // slate-300
    fontSize: '1.125rem', // lg
    lineHeight: '1.6',
  };

  const gridSectionStyle = {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
    gap: '2.5rem',
  };

  const cardStyle = {
    backgroundColor: '#1e293b', // slate-800
    padding: '1.5rem',
    borderRadius: '0.75rem',
    boxShadow: '0 1px 6px rgba(0,0,0,0.2)',
    transition: 'box-shadow 0.3s',
  };

  const cardHoverStyle = {
    boxShadow: '0 10px 20px rgba(0,0,0,0.3)',
  };

  const cardTitleStyle = {
    fontSize: '1.5rem', // 2xl
    fontWeight: '600',
    marginBottom: '0.5rem',
    color: '#3b82f6', // blue-500
  };

  const cardTextStyle = {
    color: '#cbd5e1', // slate-300
    textAlign: 'justify',
  };

  const involvementSectionStyle = {
    marginTop: '5rem',
    textAlign: 'center',
    maxWidth: '42rem',
    marginLeft: 'auto',
    marginRight: 'auto',
  };

  const involvementTitleStyle = {
    fontSize: '1.875rem', // 3xl
    fontWeight: '700',
    marginBottom: '1rem',
    color: '#3b82f6', // blue-500
  };

  const involvementTextStyle = {
    color: '#cbd5e1', // slate-300
    fontSize: '1.125rem',
  };

  const footerStyle = {
    textAlign: 'center',
    padding: '1rem',
    borderTop: '1px solid #334155', // slate-700
    color: '#94a3b8', // slate-500
    fontSize: '0.875rem',
  };

  // To handle hover on button and cards, simple trick with React state:
  const [btnHover, setBtnHover] = React.useState(false);
  const [cardHover1, setCardHover1] = React.useState(false);
  const [cardHover2, setCardHover2] = React.useState(false);

  return (
    <div style={containerStyle}>
      {/* Header */}
      <header style={headerStyle}>
        <h1 style={headerTitleStyle}>AI & DS Department</h1>
        <button
          style={btnHover ? { ...buttonStyle, ...buttonHoverStyle } : buttonStyle}
          onClick={handleStudentClick}
          onMouseEnter={() => setBtnHover(true)}
          onMouseLeave={() => setBtnHover(false)}
        >
          Students
        </button>
      </header>

      {/* Main Content */}
      <main style={mainStyle}>
        <section style={sectionCenterStyle}>
          <h2 style={heading1Style}>Welcome to Artificial Intelligence and Data Science</h2>
          <p style={paragraphStyle}>
            We are shaping the future with cutting-edge technologies in Artificial Intelligence and Data Science. Join us to innovate and lead.
          </p>
        </section>

        <section style={gridSectionStyle}>
          <div
            style={cardHover1 ? { ...cardStyle, ...cardHoverStyle } : cardStyle}
            onMouseEnter={() => setCardHover1(true)}
            onMouseLeave={() => setCardHover1(false)}
          >
            <h3 style={cardTitleStyle}>Our Vision</h3>
            <p style={cardTextStyle}>
              To emerge as a hub of excellence in AI & DS education and research, preparing students to be future innovators and leaders.
            </p>
          </div>
          <div
            style={cardHover2 ? { ...cardStyle, ...cardHoverStyle } : cardStyle}
            onMouseEnter={() => setCardHover2(true)}
            onMouseLeave={() => setCardHover2(false)}
          >
            <h3 style={cardTitleStyle}>Our Mission</h3>
            <p style={cardTextStyle}>
              To impart strong theoretical and practical knowledge in AI and Data Science, enabling students to solve real-world challenges and contribute to society.
            </p>
          </div>
        </section>

        <section style={involvementSectionStyle}>
          <h3 style={involvementTitleStyle}>Get Involved</h3>
          <p style={involvementTextStyle}>
            Participate in research, innovation, coding challenges, and tech events to grow your expertise and make a difference.
          </p>
        </section>
      </main>

      {/* Footer */}
      <footer style={footerStyle}>
        Â© {new Date().getFullYear()} Department of AI & DS, VGNT
      </footer>
    </div>
  );
};

export default DepartmentPage;
