import React from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../assets/vignan-logo.png'; // Ensure the path is correct

const Header = () => {
  const navigate = useNavigate();
  const [hoveredBtn, setHoveredBtn] = React.useState(null);

  const colors = {
    darkPurple: '#3b0076',
    mediumPurple: '#5b2a86',
    lightBlue: '#85a1c8',
    white: '#ffffff',
  };

  const buttons = [
    { key: 'home', label: 'Home', path: '/' },
    { key: 'students', label: 'Students', path: '/students' },
    { key: 'faculty', label: 'Faculty', path: '/faculty' },
    { key: 'alumni', label: 'Alumni', path: '/alumni' },
    { key: 'placements', label: 'Placements', path: '/placements' },
    { key: 'mous', label: 'MOUs', path: '/mous' },
    { key: 'events', label: 'Events', path: '/events' },
    { key: 'achievements', label: 'Achievements', path: '/achievements' },
    { key: 'contact', label: 'Contact Us', path: '/contact' },
  ];

  return (
    <header style={{
      backgroundColor: colors.darkPurple,
      color: colors.white,
      padding: '1rem 2rem',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      flexWrap: 'wrap',
    }}>
      {/* Left Section: Logo and Titles */}
      <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
        <img
          src={logo}
          alt="Vignan Logo"
          style={{
            width: '60px',
            height: '60px',
            objectFit: 'contain',
            borderRadius: '8px',
            backgroundColor: colors.white,
            padding: '5px',
          }}
        />
        <div>
          <div style={{ fontSize: '1rem', fontWeight: '600', color: colors.lightBlue }}>
            Vignan Institute of Technology and Science
          </div>
          <h1 style={{ fontSize: '1.5rem', fontWeight: '700', marginTop: '0.25rem' }}>
            Department of Artificial Intelligence and Data Science
          </h1>
        </div>
      </div>

      {/* Right Section: Navigation Buttons */}
      <div style={{ display: 'flex', gap: '0.8rem', flexWrap: 'wrap', marginTop: '1rem' }}>
        {buttons.map(({ key, label, path }) => (
          <button
            key={key}
            onClick={() => navigate(path)}
            onMouseEnter={() => setHoveredBtn(key)}
            onMouseLeave={() => setHoveredBtn(null)}
            style={{
              padding: '0.5rem 1rem',
              borderRadius: '20px',
              fontWeight: '600',
              fontSize: '0.9rem',
              border: 'none',
              cursor: 'pointer',
              backgroundColor: hoveredBtn === key ? colors.lightBlue : colors.mediumPurple,
              color: hoveredBtn === key ? colors.darkPurple : colors.white,
              boxShadow: hoveredBtn === key
                ? `0 4px 12px ${colors.lightBlue}`
                : '0 2px 8px rgba(75, 0, 118, 0.4)',
              transition: 'all 0.3s ease',
            }}
          >
            {label}
          </button>
        ))}
      </div>
    </header>
  );
};

export default Header;
