import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    // Clear any previous errors
    setError(null);

    try {
      // Send a POST request to the backend to authenticate the user
      const response = await axios.post('http://localhost:5000/api/auth/login', { email, password });

      // Check the response in the console
      console.log(response);

      // Store the JWT token in localStorage
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('name', response.data.user.name); // ðŸ‘ˆ required for dashboard greeting
      localStorage.setItem('rollNumber', response.data.user.rollNumber);

      // Redirect the user to the dashboard
      navigate('/dashboard');
    } catch (err) {
      // Handle errors (wrong credentials, server issues, etc.)
      setError('Invalid email or password');
      console.error('Login error:', err);
    }
  };

  const containerStyle = {
    height: '100vh',
    background: 'linear-gradient(135deg, #ffecd2, #fcb69f)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    fontFamily: 'Segoe UI, sans-serif',
  };

  const formStyle = {
    backgroundColor: '#ffffff',
    padding: '40px',
    borderRadius: '12px',
    boxShadow: '0 0 20px rgba(0,0,0,0.1)',
    width: '100%',
    maxWidth: '400px',
  };

  const inputStyle = {
    width: '100%',
    padding: '12px',
    margin: '10px 0',
    borderRadius: '8px',
    border: '1px solid #ccc',
    fontSize: '1rem',
  };

  const buttonStyle = {
    width: '100%',
    padding: '12px',
    marginTop: '15px',
    borderRadius: '8px',
    border: 'none',
    backgroundColor: '#ff6f61',
    color: '#fff',
    fontSize: '1rem',
    cursor: 'pointer',
    transition: '0.3s',
  };

  const linkStyle = {
    display: 'block',
    textAlign: 'center',
    marginTop: '15px',
    color: '#0077b6',
    textDecoration: 'none',
  };

  return (
    <div style={containerStyle}>
      <form onSubmit={handleLogin} style={formStyle}>
        <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Login</h2>
        
        {/* Error Message */}
        {error && <p style={{ color: 'red', textAlign: 'center' }}>{error}</p>}

        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          style={inputStyle}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={inputStyle}
          required
        />
        <button type="submit" style={buttonStyle}>Login</button>
        
        <Link to="/register" style={linkStyle}>Don't have an account? Register</Link>
      </form>
    </div>
  );
};

export default LoginPage;
