import React from 'react';
import Header from '../components/Header';

const facultyMembers = [
  { name: 'Dr. B Ravikrishna', title: 'Head of the Department' },
  { name: 'T. Sai Lalith Prasad', title: 'Assistant Professor' },
  { name: 'Lavanya Kumari', title: 'Assistant Professor' },
  { name: 'Sai Sree', title: 'Assistant Professor' },
  { name: 'Yamini', title: 'Assistant Professor' },
  { name: 'Sindhuja', title: 'Assistant Professor' },
  { name: 'Naresh', title: 'Assistant Professor' },
];

const FacultyPage = () => {
  return (
    <div style={{ backgroundColor: '#fff', minHeight: '100vh', color: '#333' }}>
      <Header />

      <div style={{ padding: '40px 20px', maxWidth: '1100px', margin: '0 auto' }}>
        <h2 style={{ textAlign: 'center', color: '#4B0082', marginBottom: '10px' }}>
          Our Faculty
        </h2>
        <p style={{ textAlign: 'center', color: '#555', maxWidth: '700px', margin: '0 auto 40px', fontSize: '1.1rem' }}>
          The Department of AI&DS is proud to have a team of dedicated and highly qualified faculty members who are passionate about teaching, research, and mentoring. They bring a blend of academic excellence and industry experience to ensure our students are equipped with the knowledge and skills required for success in the ever-evolving world of technology.
        </p>

        <div
          style={{
            display: 'flex',
            flexWrap: 'wrap',
            justifyContent: 'center',
            gap: '30px',
          }}
        >
          {facultyMembers.map((faculty, index) => {
            const isHOD = faculty.title.toLowerCase().includes('head');
            return (
              <div
                key={index}
                style={{
                  backgroundColor: isHOD ? '#fff8e1' : '#f0f0ff',
                  border: isHOD ? '3px solid #ff9800' : '2px solid #4B0082',
                  borderRadius: '12px',
                  padding: '20px',
                  width: '280px',
                  textAlign: 'center',
                  boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
                  transform: isHOD ? 'scale(1.05)' : 'none',
                }}
              >
                <h3 style={{ color: isHOD ? '#e65100' : '#4B0082', marginBottom: '10px' }}>
                  {faculty.name}
                </h3>
                <p style={{ color: isHOD ? '#ff5722' : '#1E90FF', fontWeight: '500' }}>
                  {faculty.title}
                </p>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default FacultyPage;
