import React from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../assets/vignan-logo.png'; // Make sure the logo exists at this path

const Header = () => {
  const navigate = useNavigate();
  const [hoveredBtn, setHoveredBtn] = React.useState(null);

  const colors = {
    primaryPurple: '#3b0076',
    secondaryPurple: '#5b2a86',
    primaryBlue: '#4c84a9',
    white: '#ffffff',
    lightBlue: '#e6f0ff',
  };

  

  return (
    <header style={{
      backgroundColor: colors.white,
      padding: '1rem 2rem',
      borderBottom: `2px solid ${colors.lightBlue}`,
    }}>
      {/* Header content wrapper */}
      <div style={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        flexWrap: 'wrap',
        gap: '1rem',
      }}>
        {/* Left: Logo */}
        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
          <img
            src={logo}
            alt="Vignan Logo"
            style={{
              width: '140px',
              height: '140px',
              objectFit: 'contain',
              borderRadius: '12px',
            }}
          />
          <div>
            <div style={{
              fontSize: '1.5rem',
              fontWeight: '600',
              color: colors.primaryBlue,
              lineHeight: '1.2',
            }}>
              Vignan Institute of Technology and Science
            </div>
            <h1 style={{
              fontSize: '2.2rem',
              fontWeight: '700',
              color: colors.primaryPurple,
              marginTop: '0.3rem',
              lineHeight: '1.3',
            }}>
              Department of Artificial Intelligence and Data Science
            </h1>
          </div>
        </div>
      </div>

      {/* Navigation buttons row */}
      <div style={{
        display: 'flex',
        flexWrap: 'nowrap',
        overflowX: 'auto',
        marginTop: '1.5rem',
        paddingBottom: '0.5rem',
        gap: '0.8rem',
      }}>
        {buttons.map(({ key, label, path }) => (
          <button
            key={key}
            onClick={() => navigate(path)}
            onMouseEnter={() => setHoveredBtn(key)}
            onMouseLeave={() => setHoveredBtn(null)}
            style={{
              whiteSpace: 'nowrap',
              padding: '0.6rem 1.4rem',
              borderRadius: '20px',
              fontWeight: '600',
              fontSize: '0.95rem',
              border: 'none',
              cursor: 'pointer',
              backgroundColor: hoveredBtn === key ? colors.primaryBlue : colors.secondaryPurple,
              color: colors.white,
              boxShadow: '0 2px 6px rgba(0, 0, 0, 0.15)',
              transition: 'all 0.3s ease',
              flexShrink: 0,
            }}
          >
            {label}
          </button>
        ))}
      </div>
    </header>
  );
};

export default Header;
