// src/pages/FacultyPage.js
import React from 'react';
import Header from '../components/Header';

const facultyMembers = [
  { name: 'Dr. B Ravikrishna', title: 'Head of the Department' },
  { name: 'T. Sai Lalith Prasad', title: 'Assistant Professor' },
  { name: 'Lavanya Kumari', title: 'Assistant Professor' },
  { name: 'Sai Sree', title: 'Assistant Professor' },
  { name: 'Yamini', title: 'Assistant Professor' },
  { name: 'Sindhuja', title: 'Assistant Professor' },
  { name: 'Naresh', title: 'Assistant Professor' },
];

const FacultyPage = () => {
  return (
    <div style={{ backgroundColor: '#fff', minHeight: '100vh', color: '#333' }}>
      <Header />

      <div style={{ padding: '40px 20px' }}>
        <h2 style={{ textAlign: 'center', color: '#4B0082', marginBottom: '40px' }}>
          Our Faculty
        </h2>

        <div
          style={{
            display: 'flex',
            flexWrap: 'wrap',
            justifyContent: 'center',
            gap: '30px',
          }}
        >
          {facultyMembers.map((faculty, index) => (
            <div
              key={index}
              style={{
                backgroundColor: '#f0f0ff',
                border: '2px solid #4B0082',
                borderRadius: '12px',
                padding: '20px',
                width: '280px',
                textAlign: 'center',
                boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
              }}
            >
              <h3 style={{ color: '#4B0082', marginBottom: '10px' }}>{faculty.name}</h3>
              <p style={{ color: '#1E90FF', fontWeight: '500' }}>{faculty.title}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default FacultyPage;
