import React from 'react';
import { Link } from 'react-router-dom';

const HomePage = () => {
  const containerStyle = {
    height: '100vh',
    background: 'linear-gradient(135deg, #667eea, #764ba2)',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    fontFamily: 'Segoe UI, sans-serif',
    textAlign: 'center',
    color: '#ffffff',
    padding: '0 20px',
  };

  const headingStyle = {
    fontSize: '2.8rem',
    marginBottom: '15px',
    fontWeight: 'bold',
    lineHeight: '1.4',
  };

  const subheadingStyle = {
    fontSize: '1.4rem',
    marginBottom: '40px',
    color: '#e0e0e0',
  };

  const buttonGroupStyle = {
    display: 'flex',
    gap: '20px',
    flexWrap: 'wrap',
    justifyContent: 'center',
  };

  const buttonStyle = {
    padding: '14px 32px',
    fontSize: '1rem',
    textDecoration: 'none',
    borderRadius: '8px',
    transition: 'all 0.3s ease',
    color: 'white',
    fontWeight: 'bold',
  };

  const loginButton = {
    ...buttonStyle,
    backgroundColor: '#f37272',
  };

  const registerButton = {
    ...buttonStyle,
    backgroundColor: '#36a2eb',
  };

  const hoverEffect = (e) => {
    e.target.style.transform = 'scale(1.05)';
    e.target.style.boxShadow = '0 4px 15px rgba(0,0,0,0.2)';
  };

  const removeHover = (e) => {
    e.target.style.transform = 'scale(1)';
    e.target.style.boxShadow = 'none';
  };

  return (
    <div style={containerStyle}>
      <h1 style={headingStyle}>
        Welcome to the <br />Artificial Intelligence and Data Science
      </h1>
      <p style={subheadingStyle}> Vignan Institute of Technology and Science (VGNT)</p>
      <div style={buttonGroupStyle}>
        <Link
          to="/login"
          style={loginButton}
          onMouseEnter={hoverEffect}
          onMouseLeave={removeHover}
        >
          Login
        </Link>
        <Link
          to="/register"
          style={registerButton}
          onMouseEnter={hoverEffect}
          onMouseLeave={removeHover}
        >
          Register
        </Link>
      </div>
    </div>
  );
};

export default HomePage;
