import React from 'react';
import { useNavigate } from 'react-router-dom';

const DepartmentPage = () => {
  const navigate = useNavigate();

  const handleNavigate = (path) => () => {
    navigate(path);
  };

  // Hover state for buttons & cards
  const [hoveredBtn, setHoveredBtn] = React.useState(null);
  const [hoveredCard, setHoveredCard] = React.useState(null);

  // Colors
  const colors = {
    darkPurple: '#3b0076',
    mediumPurple: '#5b2a86',
    blue: '#4c84a9',
    lightBlue: '#85a1c8',
    white: '#ffffff',
    grayText: '#4b4b4b',
  };

  // Styles
  const containerStyle = {
    minHeight: '100vh',
    backgroundColor: colors.white,
    fontFamily: "'Poppins', sans-serif",
    display: 'flex',
    flexDirection: 'column',
  };

  const headerStyle = {
    backgroundColor: colors.darkPurple,
    color: colors.white,
    padding: '1.5rem 2rem',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    flexWrap: 'wrap',
  };

  const titleWrapperStyle = {
    display: 'flex',
    flexDirection: 'column',
  };

  const collegeNameStyle = {
    fontSize: '1rem',
    fontWeight: '600',
    letterSpacing: '1.5px',
    color: colors.lightBlue,
  };

  const headerTitleStyle = {
    fontSize: '2rem',
    fontWeight: '700',
    marginTop: '0.25rem',
  };

  const buttonsContainer = {
    display: 'flex',
    gap: '1rem',
    flexWrap: 'wrap',
  };

  const buttonBase = {
    padding: '0.6rem 1.5rem',
    borderRadius: '20px',
    fontWeight: '600',
    fontSize: '0.95rem',
    border: 'none',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
    boxShadow: '0 4px 12px rgba(75, 0, 118, 0.4)',
  };

  const buttons = [
    { key: 'students', label: 'Students', path: '/students' },
    { key: 'faculty', label: 'Faculty', path: '/faculty' },
    { key: 'alumni', label: 'Alumni', path: '/alumni' },
    { key: 'placements', label: 'Placements', path: '/placements' },
    { key: 'mous', label: 'MOUs', path: '/mous' },
    { key: 'events', label: 'Events', path: '/events' },
    { key: 'achievements', label: 'Achievements', path: '/achievements' },
    { key: 'contact', label: 'Contact Us', path: '/contact' },
  ];

  const getButtonStyle = (key) => ({
    ...buttonBase,
    backgroundColor: hoveredBtn === key ? colors.lightBlue : colors.mediumPurple,
    color: hoveredBtn === key ? colors.darkPurple : colors.white,
    boxShadow: hoveredBtn === key
      ? `0 6px 20px ${colors.lightBlue}`
      : '0 4px 12px rgba(75, 0, 118, 0.4)',
  });

  const mainStyle = {
    flexGrow: 1,
    padding: '3rem 2rem',
    maxWidth: '900px',
    margin: '0 auto',
  };

  const sectionCenterStyle = {
    textAlign: 'center',
    marginBottom: '3rem',
  };

  const heading1Style = {
    fontSize: '2.5rem',
    fontWeight: '700',
    color: colors.darkPurple,
    marginBottom: '1rem',
  };

  const paragraphStyle = {
    fontSize: '1.15rem',
    color: colors.grayText,
    lineHeight: 1.6,
    maxWidth: '700px',
    margin: '0 auto',
  };

  const gridSectionStyle = {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(320px, 1fr))',
    gap: '2rem',
  };

  const cardBase = {
    backgroundColor: colors.white,
    padding: '2rem',
    borderRadius: '16px',
    boxShadow: '0 6px 20px rgba(75, 0, 118, 0.1)',
    color: colors.darkPurple,
    transition: 'transform 0.3s ease, box-shadow 0.3s ease',
    cursor: 'default',
  };

  const getCardStyle = (key) => ({
    ...cardBase,
    transform: hoveredCard === key ? 'translateY(-10px)' : 'none',
    boxShadow: hoveredCard === key
      ? `0 15px 40px rgba(75, 0, 118, 0.3)`
      : '0 6px 20px rgba(75, 0, 118, 0.1)',
  });

  const cardTitleStyle = {
    fontSize: '1.6rem',
    fontWeight: '700',
    marginBottom: '1rem',
  };

  const cardTextStyle = {
    fontSize: '1rem',
    lineHeight: 1.6,
  };

  const involvementSectionStyle = {
    marginTop: '4rem',
    textAlign: 'center',
    color: colors.grayText,
  };

  const involvementTitleStyle = {
    fontSize: '1.9rem',
    fontWeight: '700',
    marginBottom: '1rem',
    color: colors.mediumPurple,
  };

  const involvementTextStyle = {
    fontSize: '1.1rem',
    maxWidth: '650px',
    margin: '0 auto',
    lineHeight: 1.6,
  };

  return (
    <div style={containerStyle}>
      <header style={headerStyle}>
        <div style={titleWrapperStyle}>
          <div style={collegeNameStyle}>Vignan Group of Institutions</div>
          <h1 style={headerTitleStyle}>Department of AI & Data Science</h1>
        </div>
        <div style={buttonsContainer}>
          {buttons.map(({ key, label, path }) => (
            <button
              key={key}
              style={getButtonStyle(key)}
              onClick={handleNavigate(path)}
              onMouseEnter={() => setHoveredBtn(key)}
              onMouseLeave={() => setHoveredBtn(null)}
            >
              {label}
            </button>
          ))}
        </div>
      </header>

      <main style={mainStyle}>
        <section style={sectionCenterStyle}>
          <h2 style={heading1Style}>Welcome to the AI & Data Science Department</h2>
          <p style={paragraphStyle}>
            Shaping future leaders with cutting-edge innovations in Artificial Intelligence and Data Science. Join us to experience
            excellence in education, research, and real-world applications.
          </p>
        </section>

        <section style={gridSectionStyle}>
          <div
            style={getCardStyle('vision')}
            onMouseEnter={() => setHoveredCard('vision')}
            onMouseLeave={() => setHoveredCard(null)}
          >
            <h3 style={cardTitleStyle}>Our Vision</h3>
            <p style={cardTextStyle}>
              To become a center of excellence in AI & Data Science, nurturing innovators who transform society through technology.
            </p>
          </div>

          <div
            style={getCardStyle('mission')}
            onMouseEnter={() => setHoveredCard('mission')}
            onMouseLeave={() => setHoveredCard(null)}
          >
            <h3 style={cardTitleStyle}>Our Mission</h3>
            <p style={cardTextStyle}>
              Deliver quality education, foster research, and empower students with hands-on skills to solve real-world problems.
            </p>
          </div>
        </section>

        <section style={involvementSectionStyle}>
          <h3 style={involvementTitleStyle}>Get Involved</h3>
          <p style={involvementTextStyle}>
            Engage in innovative research projects, coding challenges, workshops, and community events to deepen your knowledge and impact.
          </p>
        </section>
      </main>
    </div>
  );
};

export default DepartmentPage;
