import React from 'react';
import { useNavigate } from 'react-router-dom';

const DepartmentPage = () => {
  const navigate = useNavigate();

  const handleStudentClick = () => {
    navigate('/students'); // adjust routes as needed
  };
  const handleFacultyClick = () => {
    navigate('/faculty');
  };
  const handleAlumniClick = () => {
    navigate('/alumni');
  };
  const handlePlacementsClick = () => {
    navigate('/placements');
  };
  const handleMousClick = () => {
    navigate('/mous');
  };
  const handleEventsClick = () => {
    navigate('/events');
  };
  const handleAchievementsClick = () => {
    navigate('/achievements');
  };
  const handleContactClick = () => {
    navigate('/contact');
  };

  // Styles
  const containerStyle = {
    minHeight: '100vh',
    background: 'linear-gradient(135deg, #f9f3e7, #faebd7)', // light cream tones from your referenced site
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    display: 'flex',
    flexDirection: 'column',
  };

  const headerStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '1rem 2rem',
    backgroundColor: 'rgba(255, 255, 255, 0.85)',
    boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
    borderRadius: '0 0 12px 12px',
    flexWrap: 'wrap',
    gap: '1rem',
  };

  const titleWrapperStyle = {
    display: 'flex',
    flexDirection: 'column',
  };

  const collegeNameStyle = {
    fontSize: '0.9rem',
    fontWeight: '600',
    color: '#fb8500', // orange from your color palette
  };

  const headerTitleStyle = {
    fontSize: '1.8rem',
    fontWeight: '700',
    color: '#f97316', // darker orange
    marginTop: '4px',
  };

  const buttonsContainer = {
    display: 'flex',
    flexWrap: 'wrap',
    gap: '0.8rem',
  };

  const buttonStyle = {
    backgroundColor: '#f97316',
    color: '#fff',
    padding: '0.5rem 1.25rem',
    borderRadius: '10px',
    fontWeight: '600',
    border: 'none',
    cursor: 'pointer',
    boxShadow: '0 3px 8px rgba(249, 115, 22, 0.4)',
    transition: 'background-color 0.3s, box-shadow 0.3s',
  };

  const buttonHoverStyle = {
    backgroundColor: '#d9480f',
    boxShadow: '0 6px 12px rgba(217, 72, 15, 0.6)',
  };

  const mainStyle = {
    flexGrow: 1,
    padding: '3rem 2rem',
    maxWidth: '900px',
    margin: '0 auto',
  };

  const sectionCenterStyle = {
    textAlign: 'center',
    marginBottom: '3rem',
  };

  const heading1Style = {
    fontSize: '2.25rem',
    fontWeight: '700',
    color: '#7c2d12',
    marginBottom: '0.8rem',
  };

  const paragraphStyle = {
    fontSize: '1.1rem',
    color: '#92400e',
    lineHeight: '1.5',
    maxWidth: '700px',
    margin: '0 auto',
  };

  const gridSectionStyle = {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(320px, 1fr))',
    gap: '2rem',
  };

  const cardStyle = {
    backgroundColor: 'rgba(255, 255, 255, 0.95)',
    padding: '2rem',
    borderRadius: '16px',
    boxShadow: '0 8px 25px rgba(249, 115, 22, 0.3)',
    color: '#7c2d12',
    transition: 'transform 0.3s, box-shadow 0.3s',
    cursor: 'default',
  };

  const cardHoverStyle = {
    transform: 'translateY(-6px)',
    boxShadow: '0 15px 40px rgba(249, 115, 22, 0.5)',
  };

  const cardTitleStyle = {
    fontSize: '1.5rem',
    fontWeight: '700',
    marginBottom: '1rem',
  };

  const cardTextStyle = {
    fontSize: '1rem',
    lineHeight: '1.6',
  };

  const involvementSectionStyle = {
    marginTop: '4rem',
    textAlign: 'center',
    color: '#92400e',
  };

  const involvementTitleStyle = {
    fontSize: '1.8rem',
    fontWeight: '700',
    marginBottom: '1rem',
  };

  const involvementTextStyle = {
    fontSize: '1.1rem',
    maxWidth: '650px',
    margin: '0 auto',
    lineHeight: '1.5',
  };

  // Hover states
  const [btnHover, setBtnHover] = React.useState({
    students: false,
    faculty: false,
    alumni: false,
    placements: false,
    mous: false,
    events: false,
    achievements: false,
    contact: false,
  });

  const [cardHover, setCardHover] = React.useState({ vision: false, mission: false });

  return (
    <div style={containerStyle}>
      <header style={headerStyle}>
        <div style={titleWrapperStyle}>
          <div style={collegeNameStyle}>Vignan Group of Institutions</div>
          <h1 style={headerTitleStyle}>Department of AI & Data Science</h1>
        </div>
        <div style={buttonsContainer}>
          <button
            style={btnHover.students ? { ...buttonStyle, ...buttonHoverStyle } : buttonStyle}
            onClick={handleStudentClick}
            onMouseEnter={() => setBtnHover(prev => ({ ...prev, students: true }))}
            onMouseLeave={() => setBtnHover(prev => ({ ...prev, students: false }))}
          >
            Students
          </button>
          <button
            style={btnHover.faculty ? { ...buttonStyle, ...buttonHoverStyle } : buttonStyle}
            onClick={handleFacultyClick}
            onMouseEnter={() => setBtnHover(prev => ({ ...prev, faculty: true }))}
            onMouseLeave={() => setBtnHover(prev => ({ ...prev, faculty: false }))}
          >
            Faculty
          </button>
          <button
            style={btnHover.alumni ? { ...buttonStyle, ...buttonHoverStyle } : buttonStyle}
            onClick={handleAlumniClick}
            onMouseEnter={() => setBtnHover(prev => ({ ...prev, alumni: true }))}
            onMouseLeave={() => setBtnHover(prev => ({ ...prev, alumni: false }))}
          >
            Alumni
          </button>
          <button
            style={btnHover.placements ? { ...buttonStyle, ...buttonHoverStyle } : buttonStyle}
            onClick={handlePlacementsClick}
            onMouseEnter={() => setBtnHover(prev => ({ ...prev, placements: true }))}
            onMouseLeave={() => setBtnHover(prev => ({ ...prev, placements: false }))}
          >
            Placements
          </button>
          <button
            style={btnHover.mous ? { ...buttonStyle, ...buttonHoverStyle } : buttonStyle}
            onClick={handleMousClick}
            onMouseEnter={() => setBtnHover(prev => ({ ...prev, mous: true }))}
            onMouseLeave={() => setBtnHover(prev => ({ ...prev, mous: false }))}
          >
            MOUs
          </button>
          <button
            style={btnHover.events ? { ...buttonStyle, ...buttonHoverStyle } : buttonStyle}
            onClick={handleEventsClick}
            onMouseEnter={() => setBtnHover(prev => ({ ...prev, events: true }))}
            onMouseLeave={() => setBtnHover(prev => ({ ...prev, events: false }))}
          >
            Events
          </button>
          <button
            style={btnHover.achievements ? { ...buttonStyle, ...buttonHoverStyle } : buttonStyle}
            onClick={handleAchievementsClick}
            onMouseEnter={() => setBtnHover(prev => ({ ...prev, achievements: true }))}
            onMouseLeave={() => setBtnHover(prev => ({ ...prev, achievements: false }))}
          >
            Achievements
          </button>
          <button
            style={btnHover.contact ? { ...buttonStyle, ...buttonHoverStyle } : buttonStyle}
            onClick={handleContactClick}
            onMouseEnter={() => setBtnHover(prev => ({ ...prev, contact: true }))}
            onMouseLeave={() => setBtnHover(prev => ({ ...prev, contact: false }))}
          >
            Contact Us
          </button>
        </div>
      </header>

      <main style={mainStyle}>
        <section style={sectionCenterStyle}>
          <h2 style={heading1Style}>Welcome to the AI & Data Science Department</h2>
          <p style={paragraphStyle}>
            Shaping future leaders with the latest innovations in Artificial Intelligence and Data Science. 
            Join us to experience excellence in education, research, and real-world applications.
          </p>
        </section>

        <section style={gridSectionStyle}>
          <div
            style={cardHover.vision ? { ...cardStyle, ...cardHoverStyle } : cardStyle}
            onMouseEnter={() => setCardHover(prev => ({ ...prev, vision: true }))}
            onMouseLeave={() => setCardHover(prev => ({ ...prev, vision: false }))}
          >
            <h3 style={cardTitleStyle}>Our Vision</h3>
            <p style={cardTextStyle}>
              To become a center of excellence in AI & Data Science, nurturing innovators who transform society through technology.
            </p>
          </div>

          <div
            style={cardHover.mission ? { ...cardStyle, ...cardHoverStyle } : cardStyle}
            onMouseEnter={() => setCardHover(prev => ({ ...prev, mission: true }))}
            onMouseLeave={() => setCardHover(prev => ({ ...prev, mission: false }))}
          >
            <h3 style={cardTitleStyle}>Our Mission</h3>
            <p style={cardTextStyle}>
              Deliver quality education, foster research, and empower students with hands-on skills to solve real-world problems.
            </p>
          </div>
        </section>

        <section style={involvementSectionStyle}>
          <h3 style={involvementTitleStyle}>Get Involved</h3>
          <p style={involvementTextStyle}>
            Engage in innovative research projects, coding challenges, workshops, and community events to deepen your knowledge and impact.
          </p>
        </section>
      </main>
    </div>
  );
};

export default DepartmentPage;
