import React from 'react';
import { useNavigate } from 'react-router-dom';

const DepartmentPage = () => {
  const navigate = useNavigate();

  const handleStudentClick = () => {
    navigate('/login'); // change to '/students' if you have a separate students page
  };

  return (
    <div
      className="min-h-screen text-white"
      style={{
        background: 'linear-gradient(to bottom right, #0f172a, #1e293b)',
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif"
      }}
    >
      {/* Header */}
      <header
        className="p-6 flex justify-between items-center border-b"
        style={{ borderColor: '#334155' }}
      >
        <h1 className="text-3xl font-bold text-teal-400 drop-shadow-md">
          Department of AI & DS
        </h1>
        <button
          onClick={handleStudentClick}
          className="bg-teal-500 hover:bg-teal-600 text-white px-4 py-2 rounded-md transition duration-300 shadow"
          style={{ fontWeight: 600 }}
        >
          Students
        </button>
      </header>

      {/* Main Content */}
      <main className="p-10">
        <section className="text-center mb-12">
          <h2 className="text-4xl font-bold mb-4 text-white">Welcome to the Department of Artificial Intelligence and Data Science</h2>
          <p className="text-gray-300 max-w-3xl mx-auto text-lg">
            Explore cutting-edge technologies, impactful research, and innovative projects that shape the future of AI and Data Science.
          </p>
        </section>

        <section className="grid md:grid-cols-2 gap-10">
          <div
            className="p-6 rounded-lg transition hover:shadow-lg shadow-md"
            style={{ backgroundColor: '#1e293b' }}
          >
            <h3 className="text-2xl font-semibold text-teal-300 mb-2">Our Vision</h3>
            <p className="text-gray-400 text-justify">
              To become a center of excellence in Artificial Intelligence and Data Science education, research, and innovation.
            </p>
          </div>
          <div
            className="p-6 rounded-lg transition hover:shadow-lg shadow-md"
            style={{ backgroundColor: '#1e293b' }}
          >
            <h3 className="text-2xl font-semibold text-teal-300 mb-2">Our Mission</h3>
            <p className="text-gray-400 text-justify">
              To empower students with technical knowledge and research skills to solve real-world challenges in AI and Data domains.
            </p>
          </div>
        </section>

        <section className="mt-16 text-center">
          <h3 className="text-3xl font-bold text-teal-400 mb-4">Get Involved</h3>
          <p className="text-gray-300 max-w-2xl mx-auto">
            Join our workshops, seminars, hackathons, and student chapters to deepen your expertise and build connections in the AI community.
          </p>
        </section>
      </main>

      {/* Footer */}
      <footer
        className="text-center p-4 mt-10 text-gray-500 text-sm"
        style={{ borderTop: '1px solid #334155' }}
      >
        © {new Date().getFullYear()} Department of AI & DS, VGNT
      </footer>
    </div>
  );
};

export default DepartmentPage;
