import React from 'react';
import { useNavigate } from 'react-router-dom';

const DepartmentPage = () => {
  const navigate = useNavigate();

  const handleStudentClick = () => {
    navigate('/login'); // Navigate to student portal or login
  };

  return (
    <div className="min-h-screen bg-slate-900 text-slate-100 font-sans">
      {/* Header */}
      <header className="flex justify-between items-center px-6 py-4 border-b border-slate-700">
        <h1 className="text-3xl font-bold text-blue-600">AI & DS Department</h1>
        <button
          onClick={handleStudentClick}
          className="bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded-md font-semibold text-white transition duration-200"
        >
          Students
        </button>
      </header>

      {/* Main Section */}
      <main className="px-8 py-12">
        {/* Welcome Message */}
        <section className="text-center mb-14">
          <h2 className="text-4xl font-bold text-slate-100 mb-4">
            Welcome to Artificial Intelligence and Data Science
          </h2>
          <p className="text-slate-300 max-w-3xl mx-auto text-lg">
            We are shaping the future with cutting-edge technologies in Artificial Intelligence and Data Science. Join us to innovate and lead.
          </p>
        </section>

        {/* Vision and Mission */}
        <section className="grid md:grid-cols-2 gap-10">
          <div className="bg-slate-800 p-6 rounded-lg shadow-md hover:shadow-xl transition duration-300">
            <h3 className="text-2xl font-semibold text-blue-500 mb-2">Our Vision</h3>
            <p className="text-slate-300 text-justify">
              To emerge as a hub of excellence in AI & DS education and research, preparing students to be future innovators and leaders.
            </p>
          </div>
          <div className="bg-slate-800 p-6 rounded-lg shadow-md hover:shadow-xl transition duration-300">
            <h3 className="text-2xl font-semibold text-blue-500 mb-2">Our Mission</h3>
            <p className="text-slate-300 text-justify">
              To impart strong theoretical and practical knowledge in AI and Data Science, enabling students to solve real-world challenges and contribute to society.
            </p>
          </div>
        </section>

        {/* Get Involved */}
        <section className="mt-20 text-center">
          <h3 className="text-3xl font-bold text-blue-500 mb-4">Get Involved</h3>
          <p className="text-slate-300 max-w-2xl mx-auto text-lg">
            Participate in research, innovation, coding challenges, and tech events to grow your expertise and make a difference.
          </p>
        </section>
      </main>

      {/* Footer */}
      <footer className="text-center p-4 border-t border-slate-700 text-slate-500 mt-10 text-sm">
        Â© {new Date().getFullYear()} Department of AI & DS, VGNT
      </footer>
    </div>
  );
};

export default DepartmentPage;
