import React from 'react';

const facultyList = [
  {
    name: "Dr. Jane Doe",
    designation: "Professor & Head",
    email: "jane.doe@vits.edu.in",
    phone: "+91 9876543210",
    image: "https://randomuser.me/api/portraits/women/44.jpg",
  },
  {
    name: "Dr. John Smith",
    designation: "Associate Professor",
    email: "john.smith@vits.edu.in",
    phone: "+91 9123456780",
    image: "https://randomuser.me/api/portraits/men/34.jpg",
  },
  {
    name: "Ms. Priya Sharma",
    designation: "Assistant Professor",
    email: "priya.sharma@vits.edu.in",
    phone: "+91 9988776655",
    image: "https://randomuser.me/api/portraits/women/55.jpg",
  },
  // Add more faculty here
];

const FacultyPage = () => {
  return (
    <div style={styles.pageContainer}>
      <h1 style={styles.heading}>Our Faculty</h1>
      <div style={styles.gridContainer}>
        {facultyList.map((faculty, index) => (
          <div key={index} style={styles.card}>
            <img src={faculty.image} alt={faculty.name} style={styles.image} />
            <h2 style={styles.name}>{faculty.name}</h2>
            <p style={styles.designation}>{faculty.designation}</p>
            <p style={styles.contact}>Email: <a href={`mailto:${faculty.email}`} style={styles.link}>{faculty.email}</a></p>
            <p style={styles.contact}>Phone: <a href={`tel:${faculty.phone}`} style={styles.link}>{faculty.phone}</a></p>
          </div>
        ))}
      </div>
    </div>
  );
};

const styles = {
  pageContainer: {
    background: 'linear-gradient(135deg, #5a4fcf 0%, #3b82f6 100%)',
    minHeight: '100vh',
    padding: '40px 20px',
    color: 'white',
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  },
  heading: {
    textAlign: 'center',
    marginBottom: 40,
    fontSize: '2.8rem',
    fontWeight: '700',
    letterSpacing: '1.5px',
  },
  gridContainer: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
    gap: '30px',
    maxWidth: 1200,
    margin: '0 auto',
  },
  card: {
    backgroundColor: 'rgba(255, 255, 255, 0.15)',
    borderRadius: 16,
    padding: 20,
    textAlign: 'center',
    boxShadow: '0 8px 20px rgba(0,0,0,0.3)',
    transition: 'transform 0.3s ease',
    cursor: 'default',
  },
  image: {
    width: 120,
    height: 120,
    borderRadius: '50%',
    objectFit: 'cover',
    marginBottom: 20,
    border: '3px solid #ddd',
  },
  name: {
    fontSize: '1.4rem',
    margin: '10px 0 6px',
    fontWeight: '700',
  },
  designation: {
    fontSize: '1rem',
    fontStyle: 'italic',
    marginBottom: 12,
    color: '#d1d5db', // light grayish
  },
  contact: {
    fontSize: '0.9rem',
    margin: '4px 0',
  },
  link: {
    color: 'white',
    textDecoration: 'underline',
  },
};

export default FacultyPage;
