// src/components/Header.js
import React from 'react';
import { Link } from 'react-router-dom';

const headerStyle = {
  backgroundColor: '#4B0082', // deep purple
  padding: '15px 30px',
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  color: 'white',
};

const linkStyle = {
  color: 'white',
  textDecoration: 'none',
  marginLeft: '20px',
  fontWeight: 'bold',
};

const Header = () => {
  return (
    <div style={headerStyle}>
      <div style={{ fontSize: '20px', fontWeight: 'bold' }}>AI & DS Department</div>
      <div>
        <Link to="/" style={linkStyle}>Home</Link>
        <Link to="/faculty" style={linkStyle}>Faculty</Link>
        <Link to="/placements" style={linkStyle}>Placements</Link>
        <Link to="/events" style={linkStyle}>Events</Link>
        <Link to="/achievements" style={linkStyle}>Achievements</Link>
        <Link to="/contact" style={linkStyle}>Contact Us</Link>
        <Link to="/students" style={linkStyle}>Students</Link>
      </div>
    </div>
  );
};

export default Header;
