import React from 'react';

const facultyMembers = [
  { name: 'Dr. B Ravikrishna', title: 'Head of the Department' },
  { name: 'T. Sai Lalith Prasad', title: 'Assistant Professor' },
  { name: 'Lavanya Kumari', title: 'Assistant Professor' },
  { name: 'Sai Sree', title: 'Assistant Professor' },
  { name: 'Yamini', title: 'Assistant Professor' },
  { name: 'Sindhuja', title: 'Assistant Professor' },
  { name: 'Naresh', title: 'Assistant Professor' }
];

const FacultyPage = () => {
  return (
    <div style={{ backgroundColor: '#ffffff', minHeight: '100vh', padding: '40px' }}>
      <h1 style={{
        textAlign: 'center',
        color: '#4b0082',
        fontSize: '2.5rem',
        marginBottom: '40px'
      }}>
        Our Faculty
      </h1>
      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
        gap: '20px'
      }}>
        {facultyMembers.map((faculty, index) => (
          <div key={index} style={{
            border: '2px solid #7a5cff',
            borderRadius: '12px',
            padding: '20px',
            backgroundColor: '#f0f4ff',
            textAlign: 'center',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.05)'
          }}>
            <h2 style={{ color: '#4b0082', fontSize: '1.3rem', marginBottom: '10px' }}>{faculty.name}</h2>
            <p style={{ color: '#555', fontWeight: '500' }}>{faculty.title}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FacultyPage;
