import React from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../assets/vignan-logo.png'; // ✅ make sure logo is placed here

const Header = () => {
  const navigate = useNavigate();
  const [hoveredBtn, setHoveredBtn] = React.useState(null);

  const colors = {
    primaryPurple: '#3b0076',
    secondaryPurple: '#5b2a86',
    primaryBlue: '#4c84a9',
    lightBlue: '#e6f0ff',
    white: '#ffffff',
  };

  const buttons = [
    { key: 'home', label: 'Home', path: '/' },
    { key: 'students', label: 'Students', path: '/students' },
    { key: 'faculty', label: 'Faculty', path: '/faculty' },
    { key: 'alumni', label: 'Alumni', path: '/alumni' },
    { key: 'placements', label: 'Placements', path: '/placements' },
    { key: 'mous', label: 'MOUs', path: '/mous' },
    { key: 'events', label: 'Events', path: '/events' },
    { key: 'achievements', label: 'Achievements', path: '/achievements' },
    { key: 'contact', label: 'Contact Us', path: '/contact' },
  ];

  return (
    <header style={{
      backgroundColor: colors.white,
      padding: '1.5rem 2rem',
      borderBottom: `2px solid ${colors.lightBlue}`,
    }}>
      {/* Top Row: Logo Left - Title Centered */}
      <div style={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        flexWrap: 'wrap',
        gap: '1rem',
      }}>
        {/* Logo on the left */}
        <img
          src={logo}
          alt="Vignan Logo"
          style={{
            width: '100px',
            height: '100px',
            objectFit: 'contain',
            borderRadius: '12px',
          }}
        />

        {/* Title Centered */}
        <div style={{
          flex: 1,
          textAlign: 'center',
        }}>
          <div style={{ fontSize: '1.2rem', fontWeight: '600', color: colors.primaryBlue }}>
            Vignan Institute of Technology and Science
          </div>
          <h1 style={{
            fontSize: '2rem',
            fontWeight: '700',
            color: colors.primaryPurple,
            margin: '0.3rem 0',
            lineHeight: '1.4',
          }}>
            Department of Artificial Intelligence and Data Science
          </h1>
        </div>

        {/* Empty space on the right to balance the logo */}
        <div style={{ width: '100px', height: '100px', visibility: 'hidden' }} />
      </div>

      {/* Button Row */}
      <div style={{
        display: 'flex',
        justifyContent: 'center',
        flexWrap: 'wrap',
        marginTop: '1.5rem',
        gap: '0.8rem',
      }}>
        {buttons.map(({ key, label, path }) => (
          <button
            key={key}
            onClick={() => navigate(path)}
            onMouseEnter={() => setHoveredBtn(key)}
            onMouseLeave={() => setHoveredBtn(null)}
            style={{
              padding: '0.6rem 1.4rem',
              borderRadius: '20px',
              fontWeight: '600',
              fontSize: '0.95rem',
              border: 'none',
              cursor: 'pointer',
              backgroundColor: hoveredBtn === key ? colors.primaryBlue : colors.secondaryPurple,
              color: hoveredBtn === key ? colors.white : colors.white,
              boxShadow: '0 2px 6px rgba(0, 0, 0, 0.15)',
              transition: 'all 0.3s ease',
            }}
          >
            {label}
          </button>
        ))}
      </div>
    </header>
  );
};

export default Header;
