import React from 'react';

function Home() {
  return (
    <div
      style={{
        height: '100vh',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        textAlign: 'center'
      }}
    >
      <h2 style={{ marginBottom: '20px' }}>Welcome to the Student Portal</h2>
      <img
        src="C:\Abhi\Passport_Size_Photo.jpg" // Replace with your image URL or local path
        alt="Student Portal"
        style={{ width: '300px', height: 'auto', borderRadius: '10px' }}
      />
    </div>
  );
}

export default Home;
